#!/bin/bash
src=$1
dst=$2
src_path=$(echo "$1" | sed -r "s/(.+)\/.+/\1/")
find "$src_path" -type f \( -iname "*.MUS" -o -iname "*.SND" -o -iname "*.VGA" -o -iname "GAMEPC" -o -iname "ICON.DAT" -o -iname "MENUS.DAT" -o -iname "MUSIC.DRV" -o -iname "MYLIB.FXB" -o -iname "START" -o -iname "STRIPPED.TXT" -o -iname "TABLES*" -o -iname "TBLLIST" -o -iname "TEXT*" \) -exec cp {} "$dst" \;
