#!/bin/bash
src=$1
dst=$2
src_path=$(echo "$1" | sed -r "s/(.+)\/.+/\1/")
find "$src_path" -type f \( -iname "*.MUS" -o -iname "*.SND" -o -iname "*.VGA" -o -iname "GAMEPC" -o -iname "ICON.DAT" -o -iname "INSTR.DAT" -o -iname "START" -o -iname "TABLES*" -o -iname "TBLLIST" \) -exec cp {} "$dst" \;
