#!/bin/bash
src=$1
dst=$2
src_path=$(echo "$1" | sed -r "s/(.+)\/.+/\1/")
find "$src_path" -type f \( -iname "*.ADL" -o -iname "*.CPS" -o -iname "*.DAT" -o -iname "*.DCR" -o -iname "*.DEC" -o -iname "*.EGA" -o -iname "*.INF" -o -iname "*.MAZ" -o -iname "*.PAL" -o -iname "*.SND" -o -iname "*.VCN" -o -iname "*.VMP" -o -iname "CREDITS.TXT" -o -iname "PALETTE.COL" -o -iname "FONT6.FNT" -o -iname "EOBDATA0.SAV" -o -iname "FONT8.FNT" \) -exec cp {} "$dst" \;
