#!/bin/bash
src=$1
dst=$2
find "$src" -maxdepth 1  -type d \( -iname graphics -o -iname music -o -iname patch -o -iname sound  \) -exec cp {} -r "$dst" \;

find "$src" -maxdepth 1  -type f \( -iname "*voices" -o -iname "*.bin" -o -iname "*.rsc" -o -iname "*.iaf" -o -iname "*.AD" -o -iname "*.OPL"  \) -exec cp {} "$dst" \;
