#!/bin/bash
src=$1
dst=$2
find "$src" -maxdepth 1 -type d \( -iname "DAT" -o -iname "SAVEGAME" -o -iname "WOMS" \) -exec cp -r {} "$dst" \;
find "$src" -maxdepth 1 -type f \( -iname "*.dll" -o -iname "*.mix" -o -iname "*.LS*" -o -iname "*.TRR" -o -iname "*.BNK" -o -iname "*.386" -o -iname "*.EXE" -o -iname "*.ENG" -o -iname "*.DAT" -o -iname "*.SHP" -o -iname "*.INI" -o -iname "*.HMI" \) -exec cp {} "$dst" \;
