#!/bin/bash
src=$1
find "$src" -maxdepth 1 -type d \( -iname app -o -iname commonappdata -o -iname *redist -o -iname scummvm -o -iname *support -o -iname dosbox -o -iname ega -o -iname tmp \) -exec rm -r {}  \;
find "$src" -maxdepth 1 -type f \( -iname "goggame*" -o -iname "999.*" -o -iname "994.*" \) -exec rm {}  \;
