execute.command:
if (uname -m | grep "64"); then
 arch="x86_64";
else
 arch="x86";
fi;
"$CACHE/bin/linux/$arch/libc.so.6/lzma" d "$CACHE/subarch" - | tar xvf - -C "$CACHE/" &&
"$CACHE/bin/linux/$arch/libc.so.6/lzma" d "$CACHE/deps/SDL2/SDL2_files_linux_$arch" - | tar xvf - -C "$GAMEDIR/Torchlight/"

write_file.content:
#!/bin/sh
if (uname -m | grep "64"); then
 arch="x86_64";
else
 arch="x86";
fi;
./Torchlight/Torchlight.bin.$arch
